import React, { useEffect, useState } from 'react';
import { auth } from '../firebase';

function MyProfile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Usar Firebase Authentication para acceder al usuario logueado
    const unsubscribe = auth.onAuthStateChanged((authUser) => {
      if (authUser) {
        // El usuario está logueado
        setUser(authUser);
      } else {
        // El usuario no está logueado
        setUser(null);
      }
    });

    return () => {
      // Limpia el listener cuando el componente se desmonte
      unsubscribe();
    };
  }, []);

  return (
    <>
      {/* Tu código JSX aquí */}
      {user ? (
        <div>
          <h1>Hello, {user.displayName || user.email}!</h1>
          {/* Puedes acceder a los datos del usuario, por ejemplo: */}
          <p>Correo electrónico: {user.email}</p>
          <p>ID de usuario: {user.uid}</p>
          {/* Muestra la información del perfil del usuario */}
        </div>
      ) : (
        <div>
          <h1>Hello, Invitado</h1>
          {/* Muestra contenido para invitados */}
        </div>
      )}
    </>
  );
}

export default MyProfile;
