import '../assets/styles/Styles.css';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { useHistory } from 'react-router-dom';
import { useState } from 'react';
import { db, storage } from '../firebase'; // Import your Firebase config
import { v4 as uuidv4 } from 'uuid'; // Import UUID library for generating unique IDs


function Landing({ user }) {

  const history = useHistory();

  const [postDetails, setPostDetails] =useState({
    postId:"",
    title:"",
    author:"",
    description:""
  });


  const handleChange = (e) => {
    e.persist();
    setPostDetails((oldState)=>({
      ...oldState,
      [e.target.name]: e.target.value,
    }))
  } 

  const addPost = async (e) => {
    const postTime = Date.now();
    await db.collection("post").add({
      ...postDetails
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault();


    history.push('/');
  };

  return (
    <>

    <Header/>





    <form id='post' onSubmit={handleSubmit}>
      <div>
        <label>Title</label>
        <input type="text" value={postDetails.title} onChange={handleChange} />
      </div>
      <div>
        <label>Description</label>
        <textarea value={postDetails.description} onChange={handleChange} />
      </div>

      <div>
        <label>Image</label>
        <input type="file" accept="image/*" />
      </div>

      <button type="submit">Upload</button>
    </form>

    <Footer/>
    </>
  );
}




export default Landing;
