import '../assets/styles/Styles.css';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { useState } from 'react'; // Import useState
import { db, auth } from '../firebase';
import { collection, addDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage'; // Import Firebase Storage functions

function Landing() {
  const [title, setTitle] = useState(''); // Define title and description using useState
  const [description, setDescription] = useState('');
  const [imageFile, setImageFile] = useState(null); // Define imageFile

  const addPostToFirestore = async (title, description, photoURL, uid) => {
    try {
      // Reference the "post" collection
      const postCollection = collection(db, 'post');

      // Add a new document with the post data
      const docRef = await addDoc(postCollection, {
        title,
        description,
        photoURL,
        uid,
      });

      console.log('Post added with ID: ', docRef.id);
      return docRef.id; // Return the ID of the newly added post
    } catch (error) {
      console.error('Error adding post: ', error);
      throw error; // Handle the error appropriately in your application
    }
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
  };

  const uploadImageToStorage = async (imageFile) => {
    try {
      // Create a reference to the Firebase Storage bucket and specify a unique filename
      const storageRef = ref(storage, `images/${Date.now()}_${imageFile.name}`);

      // Upload the image file to Firebase Storage
      await uploadBytes(storageRef, imageFile);

      // Get the download URL for the uploaded image
      const downloadURL = await getDownloadURL(storageRef);

      return downloadURL; // Return the download URL of the uploaded image
    } catch (error) {
      console.error('Error uploading image: ', error);
      throw error; // Handle the error appropriately in your application
    }
  };

  // Use the uploadImageToStorage function in your handleSubmit function
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Upload the image to a storage service like Firebase Storage
    if (imageFile) {
      const imageUrl = await uploadImageToStorage(imageFile);

      // Get the UID of the currently logged-in user
      const userUID = auth.currentUser.uid;

      // Add the post to Firestore with the image URL and user UID
      await addPostToFirestore(title, description, imageUrl, userUID);
    }
  };

  return (
    <>
      <Header />

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input type="text" id="title" name="title" onChange={handleTitleChange} value={title} />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea id="description" name="description" onChange={handleDescriptionChange} value={description}></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="image">Image</label>
          <input type="file" id="image" name="image" accept="image/*" onChange={handleImageChange} />
        </div>
        <button type="submit">Upload</button>
      </form>

      <Footer />
    </>
  );
}

export default Landing;
