import React, { useEffect, useState } from 'react';
import { auth } from '../firebase';

function MyProfile() {
  const [user, setUser] = useState(null);

  
  useEffect(() => {
    // Use Firebase Authentication to access the logged-in user
    const unsubscribe = auth.onAuthStateChanged((authUser) => {
      if (authUser) {
        // User is logged in
        setUser(authUser);
      } else {
        // User is not logged in
        setUser(null);
      }
    });

    return () => {
      // Clean up the listener when the component unmounts
      unsubscribe();
    };
  }, []);


  const handleSignOut = () => {
    // Cerrar sesión (sign out) al hacer clic en el botón
    auth.signOut().then(() => {
      // Redirige al usuario a la página de inicio de sesión u otra página si es necesario
      // Por ejemplo, puedes usar history.push('/login') para redirigir a la página de inicio de sesión
    });
  };

  return (
    <>
      {user ? (
        <div>
          <h1>Hello, {user.displayName || user.email}!</h1>
          <p>Email: {user.email}</p>
   
            {/* Botón para cerrar sesión */}
            <button onClick={handleSignOut}>Sign Out</button>
        </div>
      ) : (
        <div>
          <h1>Hello, Guest</h1>
          {/* Muestra contenido para invitados */}
        </div>
      )}
    </>
  );
}

export default MyProfile;
