import '../assets/styles/Styles.css';
import statelessLogo75 from '../assets/img/statelessLogo75.png';
import { useState } from 'react';
import { signInWithEmailAndPassword } from 'firebase/auth'; // Corrected import
import { auth } from '../firebase'; // Import auth from your firebase.js file
import { useHistory } from 'react-router-dom';

function SignIn() {
  const [userID, setUserID] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSignIn = async (e, type) => {
    e.preventDefault();

    // Use the state variables instead of accessing e.target
    try {
      const userCredential = await signInWithEmailAndPassword(auth, userID, password);
      // You can access userCredential.user for the user object if needed
      history.push('/');
    } catch (err) {
      alert(err.code);
    }
  }

  return (
    <>
      <div id="container">
        <header id='header-signin'>
          <div>
            <a href='/'><img src={statelessLogo75} alt='Stateless logo' /></a>
          </div>
        </header>

        <form id="sign-in-form" onSubmit={handleSignIn}>
          <div>
            <h1>Welcome to Stateless!</h1>
          </div>

          <div className="form-group">
            <label htmlFor="fusername">Username/Email</label>
            <input
              type="text"
              id="fusername"
              name="fusername"
              placeholder='Enter your username or email'
              value={userID}
              onChange={(e) => setUserID(e.target.value)}
            />
          </div>

          <div className="form-group">
            <label htmlFor="lpassword">Password</label>
            <input
              type="password"
              id="lpassword"
              name="lpassword"
              placeholder='Enter your password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <a href='/'>Forgot password?</a>

          <div id='buttons-su'>
            <button id='button-su' type="submit">Sign in</button>

            <button type="button" className="login-with-google-btn">
              Sign in with Google
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

export default SignIn;
