import '../assets/styles/Styles.css';
import statelessLogo75 from '../assets/img/statelessLogo75.png';
import { useState } from 'react';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { db } from '../firebase';
import { useHistory } from 'react-router-dom';

function SignIn(){


    const [userID, setUserID] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();


    const handleSignIn = async (e,type) => {
        e.preventDefault();

        const userID = e.target.userID.value;
        const password = e.target.password.value;


        signInWithEmailAndPassword(db,userID,password).then(data=>{
            history("/");
        })
    
    }

return(


    <>
<div id="container">
     <header id='header-signin'>
        <div>
          <a href='/'><img src={statelessLogo75}  alt='Stateless logo'></img></a>
        </div>
     </header>


     <form id="sign-in-form" onSubmit={handleSignIn}>

            <div>
                <h1>Welcome to Stateless!</h1>
            </div>

          <div className="form-group">
            <label htmlFor="fusername">Username/Email</label>
            <input
              type="text"
              id="fusername"
              name="fusername"
              placeholder='Enter your username or email'
              value={userID}
              onChange={(e) => setUserID(e.target.value)}
            />
          </div>

          <div className="form-group">
            <label htmlFor="lpassword">Password</label>
            <input
              type="password"
              id="lpassword"
              name="lpassword"
              placeholder='Enter your password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

            <a href='/'>Forgot password?</a>

          <div id='buttons-su'>

            <button id='button-su' type="submit">Sign in</button>


            

            <button type="button" class="login-with-google-btn" >
              Sign in with Google
            </button>
          </div>

        </form>
    </div>
    </>
    
);
}

export default SignIn;
