import '../assets/styles/Styles.css';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { useState } from 'react'; // Import useState
import { db } from '../firebase';
import { collection, addDoc } from 'firebase/firestore';

function Landing() {
  const [title, setTitle] = useState(''); // Define title and description using useState
  const [description, setDescription] = useState('');
  const [imageFile, setImageFile] = useState(null); // Define imageFile

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Upload the image to a storage service like Firebase Storage
    if (imageFile) {
      // Define or import the uploadImageToStorage function and use it here
      // const imageUrl = await uploadImageToStorage(imageFile);
    }
  };

  return (
    <>
      <Header />

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input type="text" id="title" name="title" onChange={handleTitleChange} value={title} />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea id="description" name="description" onChange={handleDescriptionChange} value={description}></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="image">Image</label>
          <input type="file" id="image" name="image" accept="image/*" onChange={handleImageChange} />
        </div>
        <button type="submit">Upload</button>
      </form>

      <Footer />
    </>
  );
}

export default Landing;
