import '../assets/styles/Styles.css';
import Header from '../components/Header';
import Footer from '../components/Footer';

import { useState } from 'react';
import { db, storage } from '../firebase'; // Import your Firebase config
import { v4 as uuidv4 } from 'uuid'; // Import UUID library for generating unique IDs

function UploadForm({ user }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [imageFile, setImageFile] = useState(null);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (imageFile) {
        // Generate a unique post ID (you can also use your own logic)
        const postId = uuidv4();

        // Upload image to Firebase Storage
        const storageRef = storage.ref(`images/${postId}_${imageFile.name}`);
        await storageRef.put(imageFile);
        const imageUrl = await storageRef.getDownloadURL();

        // Save the post data to Firestore
        await db.collection('post').add({
          uid: user.uid, // Assuming you have a user object with a uid
          postId,
          image: imageUrl,
          title,
          description,
          timestamp: new Date(),
        });

        // Reset form fields after successful upload
        setTitle('');
        setDescription('');
        setImageFile(null);
      }
    } catch (error) {
      console.error('Error uploading data:', error);
      // Handle and display the error to the user
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title</label>
        <input type="text" value={title} onChange={handleTitleChange} />
      </div>
      <div>
        <label>Description</label>
        <textarea value={description} onChange={handleDescriptionChange} />
      </div>
      <div>
        <label>Image</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>
      <button type="submit">Upload</button>
    </form>
  );
}




export default Landing;
