import '../assets/styles/Styles.css';
import { useHistory } from 'react-router-dom';

import { collection, doc, setDoc, getDoc } from 'firebase/firestore';

import { auth,db } from '../firebase';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { useState } from 'react';

function SignUp() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');

  const history = useHistory();


  const handleSignUp = async (e) => {
    e.preventDefault();
  
    const emailValue = email;
    const passwordValue = password;
    const usernameValue = username; // Agrega el nombre de usuario desde el input
  
    try {
      // Verifica si el nombre de usuario ya est치 en uso en Firestore
      const userDoc = await getDoc(doc(collection(db, 'user'), usernameValue));
      if (userDoc.exists()) {
        console.error('Username already in use');
        // Muestra un mensaje de error al usuario
        return;
      }
  
      // Crea una cuenta de usuario en Firebase
      const userCredential = await createUserWithEmailAndPassword(auth, emailValue, passwordValue);
  
      // Almacena informaci칩n adicional del usuario en Firestore
      const user = userCredential.user;
      await setDoc(doc(collection(db, 'user'), usernameValue), {
        uid: user.uid,
        username: usernameValue,
        email: emailValue,
      });
  
      // Si el registro es exitoso, redirige al usuario
      history.push('/');
    } catch (error) {
      // Maneja errores de registro
      console.error('Error registering:', error.message);
    }
  };

  return (
    <>
      <div id='header-signup'>
        <div id="got-account">
          <h4>Already got an account?</h4><a href="/sign-up">Sign in!</a>
        </div>
      </div>

      <div id="sign-up">

        <div id="title-sup">
          <h1>Sign Up </h1>
          <h4>It's quick and easy!</h4>
        </div>

        <form onSubmit={handleSignUp}>
          <div className="form-group">
            <label htmlFor="fusername">Username</label>
            <input
              type="text"
              id="fusername"
              name="fusername"
              placeholder='Enter a username'
              value={username}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email address</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder='Enter your email address'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className="form-group">
            <label htmlFor="lpassword">Password</label>
            <input
              type="password"
              id="lpassword"
              name="lpassword"
              placeholder='Enter a password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <div className="form-group">
            <label htmlFor="lcpassword">Confirm password</label>
            <input
              type="password"
              id="lcpassword"
              name="lcpassword"
              placeholder='Confirm password'
            />
          </div>

          <input type="submit" value="Create account" />
        </form>
      </div>
    </>
  );
}

export default SignUp;
